const discord = require("discord.js")
let tickets = require("./../../server.js")
const ticket = new tickets()
module.exports = {
  name: "add",
  aliases: [],
  description: "Add someone to the ticket",
  run: async (client, message, args) => {
    let chan = ticket.addMember({
      message: message,  //The way you defined message in the message event
      user: message.mentions.members.first()
    })
    
    message.channel.send(`${message.author} have claimed the ticket`)
  }
}