const discord = require("discord.js")
let tickets = require("./../../server.js")
const ticket = new tickets()
module.exports = {
  name: "remove",
  aliases: [],
  description: "Add someone to the ticket",
  run: async (client, message, args) => {
    let chan = ticket.removeMember({
      message: message, 
      user: message.mentions.members.first() //User to be removed from the ticket
    })
    
    message.channel.send(`${message.author} have rejoved ${message.mentions.members.first()} from the ticket succesfully`)
  }
}